interface TodoModel {
    id: string;
    dwsOrderStatus: string | null;
    text: string | null;
    completed: boolean | null;
}

export interface HttpResponse<T> {
    readonly ok: boolean;
    readonly status: number;
    readonly body: T | null;
}

declare const window: any;

export const useApiClient = () => {
     
    const baseUrl = "https://localhost:7192/";
    const todoApriUrl = baseUrl + "Todo";

    //async function getTodos() {

    //    let response: HttpResponse<TodoModel[]>;
    //    response = await SendTodos("GET", todoApriUrl);
    //    return response.status === 200 ? await response.body as TodoModel[]  : null;
    //}

    async function getTodos(): Promise<HttpResponse<TodoModel[]>> {
        return sentHttpRequest<TodoModel[]>(
            "GET",
            todoApriUrl
        );
    }

    async function sentHttpRequest<T>(
        verb: string,
        url: string,
        payload?: any
    ): Promise<HttpResponse<T>> {
        var request = new Request(url, {
            method: verb,
            body: payload ? undefined : JSON.stringify(payload),
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json"
            },
        });

        const fetchResponse = await fetch(request);

        const response = {
            body: await fetchResponse.json(),
            ok: fetchResponse.ok,
            status: fetchResponse.status,
        } as HttpResponse<T>;

        try {
            if (!response.ok) {
                if (response.status !== 422 && response.status !== 404) {
                    console.log("Error on api");
                }
            }
        } catch (error) {
            throw error;
        }

        return response;
    }


    async function sendAnonymousRequest(
        verb: string,
        url: string,
        payload?: any
    ) {
        let request = {
            method: verb,
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
                "Client-Version": "v1",
                "Access-Control-Allow-Origin": '*',
                "Access-Control-Allow-Headers": "X-Requested-With",
                "mode": 'no-cors'
            },
        } as any;
     
        if (payload) request.body = JSON.stringify(payload);

        const response = await fetch(url, request);
        console.log(response);
        return response;
    }

    return {
        getTodos
    };
};
