interface TodoModel {
    id: string;
    dwsOrderStatus: string | null;
    text: string | null;
    completed: boolean | null;
}

export interface HttpResponse<T> {
    readonly ok: boolean;
    readonly status: number;
    readonly body: T | null;
}

declare const window: any;

export const useApiClient = () => {

 
    const baseUrl = window.env.api;
    const todoApriUrl = baseUrl + "Todo";


    async function getTodos() {
        console.log(todoApriUrl);
        const response = await sendAnonymousRequest("GET", todoApriUrl);

        return response.status === 200 ? await response.json() : null;
    }


    async function sendAnonymousRequest(
        verb: string,
        url: string,
        payload?: any
    ) {
        let request = {
            method: verb,
            headers: {
                Accept: "application/json",
                "Content-Type": "application/json",
                "Client-Version": process.env.REACT_APP_VERSION,
            },
        } as any;

        if (payload) request.body = JSON.stringify(payload);

        const response = await fetch(url, request);

        return response;
    }

    return {
        getTodos
    };
};
